# представление №2

CREATE VIEW v_clients AS
select
	CONCAT(c.first_name," ",c.last_name) AS name,                                 #имя клиента
    (select name from cars_brand where id = car.brand_id) AS Auto_Brand,        #авто клиента - производитель
    (SELECT name FROM cars_model where id=car.model_id) AS model,               #модель автомобиля
    car.gos_number,                                                             #рег номер авто ( гос номер)
    car.vin_num,                                                                # VIN номео авто/кузова
    c.email,                        # email клиента
    c.phone,                        # номе телефона клиента
    c_stat.name AS 'status',        # статус клиента
    CONCAT (
		adr.zipcode, ", ",
        adr.region,", ",            # адрес клиента
        adr.city,", ",
        adr.street,", ",
        adr.house_num," - ",
        adr.flat_num) AS address

	from clients AS c
    JOIN cars_clients AS car
    ON c.id = car.client_id
    JOIN clients_status AS c_stat
	ON c_stat.id=client_status_id
    JOIN addresses AS adr
    ON adr.id=c.address_id
    order by name;

